//è·å–è±†ç“£èµ„æº,è¿”å›jsonæ•°æ®
function getDoubanRes(url) {
    //log(url)
    var cc =null;
    if($.type(url)=='object'){
         cc=url.cc;
         url = url.url;
    }
    var _url = url;
    if (url.indexOf('apikey') === -1) {
        _url += (url.indexOf('?') === -1 ? '?' : '&') + "apikey=0dad551ec0f84ed02907ff5c42e8ec70"
    }
    let _ts = parseInt(Date.now()/1000).toString();
    let m = 'GET&' + encodeURIComponent(url.split('douban.com')[1].split('?')[0]) + '&' + _ts;
    eval(getCryptoJS());
    let _sig = hexToBase64(CryptoJS.HmacSHA1(m,'bf7dddc7c9cfe6f7').toString());
    _url += '&_sig=' + _sig +'&_ts=' + _ts;
    //let cookieCache = 'hiker://files/cache/doubancookie.txt';
    let s = fetch(_url, {
        headers: {
            //'Cookie': fetch(cookieCache),
            "User-Agent": "Rexxar-Core/0.1.3 api-client/1 com.douban.frodo/7.9.0.beta2(215) Android/25 product/TAS-AL00 vendor/HUAWEI model/TAS-AL00  rom/android  network/wifi  platform/mobile com.douban.frodo/7.9.0.beta2(215) Rexxar/1.2.151  platform/mobile 1.2.151"
        },
        method: 'GET'
        //body: 'host=frodo.douban.com'
    });
    let json = JSON.parse(s);
    if(json.localized_message){
        cc= cc==null ? 1 : cc+1;
        log('é‡è¯•æ¬¡æ•°:'+cc);
        if(cc>5){throw new Error('è¶…æ—¶')}
        java.lang.Thread.sleep(1000);
        return getDoubanRes({url:url,cc:cc});
    }else{
        return json;
    }
}

