function isBadVideo(videoUrl) {//æ˜¯åçš„è§†é¢‘
    let dmPath = 'hiker://files/cache/diaomao.txt';//åŠæ¯›æ¨¡å¼è¿‡æ»¤æ–‡ä»¶è·¯å¾„
    let dmUrls;
    try {
        dmUrls = request(dmPath).trim().split('\n').filter(it => it && !it.startsWith('//'));
    } catch (e) {
        dmUrls = [];
    }

    function hasDm(videoUrl, dmUrls) {//æ’­æ”¾åœ°å€æ˜¯å¦ä¸ºåŠæ¯›è§†é¢‘
        if (dmUrls.length < 1) {
            return false
        }
        function print(str){
            if(typeof(log)!=='undefined'){
                log(str);
            }else{
                fba.log(str);
            }
        }
        for (let dmUrl of dmUrls) {
            if ((new RegExp(dmUrl)).test(videoUrl)) {//æ’­æ”¾åœ°å€åŒ…å«åŠæ¯›æ ‡å¿—çš„åœ°å€
                print('åŠæ¯›è§†é¢‘,è‡ªåŠ¨è¿‡æ»¤:'+videoUrl);
                return true
            }
        }
        print('éåŠæ¯›è§†é¢‘,å¯æ’­æ”¾:'+videoUrl);
        return false
    }

    function isDm(ret) {
        if (!ret) {
            return true
        }
        return hasDm(ret.split('#')[0].split(';')[0], dmUrls)
    }
    return isDm(videoUrl)
}

