    //TVÁ∫øË∑Ø//FSTÁ∫øË∑Ø//VOEÁ∫øË∑Ø//STÁ∫øË∑Ø//DSÁ∫øË∑Ø//VTUÁ∫øË∑Ø
    var id = input.split('').reverse().join('');
    var url = `https://lk1.supremejav.com/supjav.php?c=${id}`;
    var referer = `https://lk1.supremejav.com/supjav.php?l=${input}`;
    var location = JSON.parse(fetch(url, {
        headers: {
            "Referer": referer,
        },
        onlyHeaders: true
    })).url.replace(/#.*/, "")
    log(location)

    if (location.includes("emturbovid")) {

        //‚ë†TVÁ∫øË∑Ø
        var domain = getHome(location)
        var path = fetch(location, {
            headers: {
                'Referer': referer
            }
        }).match(/'.*?(\/.*?\.m3u8)/)[1]
        var playUrl = path.includes("com") ? "https:" + path : getHome(location) + path
        return playUrl + ";{Referer@" + location + "}"

    } else if (/cindyeyefinal|fc2stream|michaelapplysome/.test(location)) {

        //‚ë°FSTÁ∫øË∑Ø
        var html = fetch(location, {
            headers: {
                'Referer': referer
            }
        });
        var script = pdfh(html,"body>script&&Html").replace("eval","")
        var uas = eval(script)
        var jwplayer = uas.match(/"(https?.*m3u8.*?)"/)[1]    
       //log(jwplayer)  
        return jwplayer + ";{Referer@" + location + "}"

    } else if (location.includes("streamtape")) {

        //‚ë¢STÁ∫øË∑Ø
        var html = fetch(location, {
            headers: {
                'Referer': referer
            }
        })
        var pattern = html.match(/\('#(.*)'\)/)[1]
        var src = "var srclink = " + html.match(new RegExp("'" + pattern + ".*?=([^;]+)"))[1]
        eval(src)
        var link = "https:" + srclink + "&stream=1"
        var urlPlay = JSON.parse(fetch(link, {
            headers: {
                "Referer": location,
            },
            onlyHeaders: true
        })).url
        return urlPlay + ";{Referer@" + location + "}"

    } else if (/d0000d|dood|ds2play/.test(location)) {

        //‚ë£DSÁ∫øË∑Ø
        var html = fetch(location, {
            headers: {
                'Referer': referer
            }
        })
        var pass_md5 = getHome(location) + html.match(/\/pass_md5[^"']+/)[0]
        var token = html.match(/cookieIndex.*?\'(.*?)\'/)[1]
        var data = fetch(pass_md5, {
            headers: {
                "Referer": location
            }
        })

        function makePlay(token) {
