function getObjfuc(obj) {
    const methods = Object.getOwnPropertyNames(obj).filter(
        (name) => typeof obj[name] === "function"
    );
    if (methods.length == 0) {
        console.log(obj.toString());
        return;
    }
    const formattedString = methods
        .map((name) => {
            const fn = obj[name];
            const fnString = `${name}: ${fn.toString()}`;
            const restoredFnString = fnString.replace(
                /\\u([\d\w]{4})/gi,
                function(match, grp) {
                    return String.fromCharCode(parseInt(grp, 16));
                }
            );
            return restoredFnString;
        })
        .join("\n");
    console.log(formattedString);
}

