        var list = [];
        if (value.includes(';')) {
            list = value.split(';');
        } else {
            list = [value];
        }
        list.forEach(x => {
            var index = x.indexOf("=");
            var key = x.substring(0, index).trim();
            var value = x.substring(index + 1);
            cookies[key] = value;
        })
    }
});

