        let url = 'hiker://empty'
        const player = pdfh(info, '#player-width&&Html')
        const iframe = player.match(/id="iframe"/)
        if (iframe) {
            let iframe_context = ''
            let iframe_src = ''
            let script = pdfh(player, 'script&&Html').replace('return p', 'iframe_context = p')
            eval(script)
