    let hasKey = videoKeys.test(rUrl)||videoKeys.test(rurl2);
    let parUrl = pUrl.replace(pUrl.split("?")[0],''); // ÂàÜÂâ≤ÈóÆÂè∑ÂêéÂâ©‰ΩôÂèÇÊï∞ÁöÑÂÆåÊï¥ÈìæÊé•
