        hasKey = hasKey||videoKeys.test(rUrl3);
    }
    if(hasKey&&!excKey){
        let tips = 'Ê£ÄÊµãÂà∞Áñë‰ººÂ§öÂ™í‰ΩìÁöÑÂú∞ÂùÄ:';
