function isBadVideo(videoUrl) {//ÊòØÂùèÁöÑËßÜÈ¢ë
    let dmPath = 'hiker://files/cache/diaomao.txt';//ÂêäÊØõÊ®°ÂºèËøáÊª§Êñá‰ª∂Ë∑ØÂæÑ
    let dmUrls;
    try {
        dmUrls = request(dmPath).trim().split('\n').filter(it => it && !it.startsWith('//'));
    } catch (e) {
        dmUrls = [];
    }

    function hasDm(videoUrl, dmUrls) {//Êí≠ÊîæÂú∞ÂùÄÊòØÂê¶‰∏∫ÂêäÊØõËßÜÈ¢ë
        if (dmUrls.length < 1) {
            return false
        }
        function print(str){
            if(typeof(log)!=='undefined'){
                log(str);
            }else{
                fba.log(str);
            }
        }
        for (let dmUrl of dmUrls) {
            if ((new RegExp(dmUrl)).test(videoUrl)) {//Êí≠ÊîæÂú∞ÂùÄÂåÖÂê´ÂêäÊØõÊ†áÂøóÁöÑÂú∞ÂùÄ
                print('ÂêäÊØõËßÜÈ¢ë,Ëá™Âä®ËøáÊª§:'+videoUrl);
                return true
            }
        }
        print('ÈùûÂêäÊØõËßÜÈ¢ë,ÂèØÊí≠Êîæ:'+videoUrl);
        return false
    }

    function isDm(ret) {
        if (!ret) {
            return true
        }
        return hasDm(ret.split('#')[0].split(';')[0], dmUrls)
    }
    return isDm(videoUrl)
}

