    let XBPQAdapter = $.require("XBPQAdapter");
    if (typeof source.ext === "string") {
      source.ext = runtimeConfig.getAbsolutePath(source.ext);
    }
    let xbpq = new XBPQAdapter(source);

    xbpq.init();
    drpyMap.set(source.key, xbpq);
    return xbpq;
  } else if (source.api === "csp_AppYsV2") {
    let AppYsV2Adapter = $.require("AppYsV2Adapter");

    let appYsV2 = new AppYsV2Adapter(source);
    appYsV2.init();
    drpyMap.set(source.key, appYsV2);
    return appYsV2;
  } else {
    return createT3(source);
  }
}

