function replaceKeysInArray(array, keyMap) {
  keyMap = {
    'Êî∂Ëóè': 'ÊàëÁöÑ',
  };
  array.forEach(item => {
    Object.keys(item).forEach(key => {
      if (keyMap.hasOwnProperty(item[key])) {
        item[key] = keyMap[item[key]];
      }
    });
  });
}

