function getLineShow(ef) {
  //log(ef)
  let obj = {
    s: ef,
    line: ef.from.indexOf(ef.from[ef.line]),
    lines: ef.from.length,
    from: ef.from[ef.line],
    plays: ef.plays[ef.line],
    spage: ef.spagenum,
    max: ef.max[ef.line]
  }
  function Template(template, data) {
    // Áî®‰∫éÊâßË°åÂ≠óÁ¨¶‰∏≤‰∏≠ÁöÑË°®ËææÂºè
    function evaluateExpression(expr) {
      try {
        // ‰ΩøÁî® with ËØ≠Âè•Â∞Ü data ÂØπË±°ÊîæÂÖ•ÂΩìÂâç‰ΩúÁî®Âüü
        // ËøôÊ†∑ÂèØ‰ª•Âú®Ë°®ËææÂºè‰∏≠Áõ¥Êé•‰ΩøÁî® data ÁöÑÂ±ûÊÄß
        with (data) {
          return eval(expr);
        }
      } catch (e) {
        return expr; // Â¶ÇÊûúË°®ËææÂºèÊó†Ê≥ïËÆ°ÁÆóÔºåÂàôËøîÂõûÂéüÂßãÂ≠óÁ¨¶‰∏≤
      }
    }
    // Ê≠£ÂàôË°®ËææÂºèÂåπÈÖç { Ë°®ËææÂºè } Ê†ºÂºèÁöÑÂç†‰ΩçÁ¨¶
    return template.replace(/{(.*?)}/g, (match, expr) => {
      //console.log(expr)
      // ÊâßË°åË°®ËææÂºè
      return evaluateExpression(expr);
    });
  }
  let lshow = Template(ui_config.er.Á∫øË∑ØÊòæÁ§∫, obj);
  return lshow;
}

/** 
 * ui $Êâ©Â±ï
*/
function ui_expand() {
  $.extend({
    deepMerge,
    themes: Theme,
    swapArrayElements(array, index1, index2) {
      let result = array.slice();
      index1 = array.findIndex(x => x.index == index1);
      index2 = array.findIndex(x => x.index == index2);
      // ‰ΩøÁî®‰∏¥Êó∂ÂèòÈáè‰∫§Êç¢‰∏§‰∏™ÂÖÉÁ¥†
      const temp = result[index1];
      result[index1] = result[index2];
      result[index2] = temp;
      return result;
    },
    uiConfig: {
      uc: ui_fig,
      def: def,
      deepObjectProperty(obj, path, value) {
        var parts = path.split('.');
        var current = obj;
        for (var i = 0; i < parts.length - 1; i++) {
          var part = parts[i];
          if (!current[part]) {
            current[part] = {};
          }
          current = current[part];
        }
        current[parts[parts.length - 1]] = value;
      },
      get() {
        let uc = storage0.getItem("ui_config", this.uc);
        if (Object.keys(uc).length == 0) {
          uc = this.def;
        }
        return uc;
      },
      set(value, key) {
        let uc = this.get();
        if (key) {
          this.deepObjectProperty(uc, key, value);
        } else {
          uc = value;
        }
        storage0.setItem("ui_config", uc);
      },
      getIndexs() {
        let uc = this.get();
        if (uc.yi.ui.length > 0 && uc.yi.ui.some(x => Object.keys(x).length > 0)) {
          return uc.yi.ui;
        } else {
          this.set(this.items, "yi.ui");
          return this.items;
        }
      },
    },
    uiTheme: {
      self: ui_theme,
      def: Theme[theme],
      deepObjectProperty(obj, path, value) {
        var parts = path.split('.');
        var current = obj;
        for (var i = 0; i < parts.length - 1; i++) {
          var part = parts[i];
          if (!current[part]) {
            current[part] = {};
          }
          current = current[part];
        }
        current[parts[parts.length - 1]] = value;
      },
      get(key, def) {
        let ut = storage0.getItem("ui_theme", this.self);
        if (key != undefined) {
          return ut[key] || (def || "")
        }
        if (Object.keys(ut).length == 0) {
          uc = this.def;
        }
        return ut;
      },
      set(value, key) {
        let ut = this.get();
        if (key) {
          this.deepObjectProperty(ut, key, value);
        } else {
          ut = value;
        }
        storage0.setItem("ui_theme", ut);
      },
    }
  })
}

