function htmlEntitiesToText(htmlString) {
  // ÂÆö‰πâ‰∏Ä‰∏™ HTML ÂÆû‰ΩìÂà∞Â≠óÁ¨¶ÁöÑÊò†Â∞ÑË°®
  const entities = {
