function removeDuplicatesByValue(arr, valueToCheck) {
  // ‰ΩøÁî® Set ÂØπË±°Êù•Â≠òÂÇ®Â∑≤ÁªèÈÅáÂà∞ÁöÑÂÄº
  const seenValues = new Set();
