  },
  normalizeUrl(url) {
    url = url.replace(/\\\//g, '/').replace("&#038;", "&");
    // Ê≠£ÂàôË°®ËææÂºèÂåπÈÖçÂçèËÆÆÂíå‰∏ªÊú∫ÂêçÔºå‰ª•ÂèäÁ¨¨‰∏Ä‰∏™ÊñúÊù†
    const regex = /^((?:[a-zA-Z]+:\/\/)?(?:[^\s/]+\.[^\s/]+))(\/?[^\s]*)?/;
    // ÊçïËé∑ÂçèËÆÆÂíå‰∏ªÊú∫Âêç
    if (url.startsWith("//")) {
      if (this.rule.URL != null) {
        url = this.rule.URL.protocol + ":" + url;
      } else {
        url = "https:" + url;
      }
    }
    if (url.startsWith("/")) {
      url = this.rule.host + url;
    }
    const match = url.match(regex);
    if (match) {
      // ÂçèËÆÆÂíå‰∏ªÊú∫Âêç
      const protocolHost = match[1];
      // Ë∑ØÂæÑÔºåÂ¶ÇÊûúÊúâÁöÑËØù
      const path = match[2] || '';
      // ÂéªÈô§Ë∑ØÂæÑ‰∏≠ÁöÑÂ§ö‰ΩôÊñúÊù†ÔºåÁÑ∂ÂêéÈáçÊñ∞ÁªÑÂêàURL
      return protocolHost + path.replace(/\/+/g, '/');
    }
    return url; // Â¶ÇÊûúÊ≤°ÊúâÂåπÈÖçÔºåËøîÂõûÂéüÂßãURL
  },
  judgeUrl(url, name, host, type) {
    let result = false;
    if (type == "‰∏ÄÁ∫ß") {
      if (!url) {
        return true;
      }
      if (/^(‰ºöÂëò‰∏≠ÂøÉ)$/.test(name)) {
        return true;
      }
      //log(url)
      if (/vod\/type\/id\/\d+\.html|\/label\/(hot|down|live|web|top).html|user\/login/.test(url) || /^#$/.test(url)) {
        log("ÂëΩ‰∏≠:", url)
        result = true;
      }
      if (/index.php\/vod\/show\//.test(url) && /\/id\/\d+\.html/.test(url)) {
        return true;
      }

      if (/detail\/id\/\d+\.html|voddetail/gi.test(url)) {
        return false;
      }
      if (/javascript:|\/vod\/?type\/|\/vod\/?show\//.test(url)) {
        result = true;
      }
      if (url != null && url.replace(/\/$/, "") == host) {
        result = true;
      }
    }
    //log("result",url)
    return result;
  },
