          list = XBPQParse.getSplitArray(html, v);
          if (key == "Á∫øË∑ØÊï∞ÁªÑ") {
            let autoc = this.AutoContent();
            if (autoc.isEmpty(["Á∫øË∑ØÊ†áÈ¢ò"])) {
