  setSwitchs(switchs) {
    function checkDigit(str, key) {
      const pattern = new RegExp(`${key}(\\d+)`);
      const match = str.match(pattern);
      return match ? parseInt(match[1], 10) : null; // ËøîÂõûÊï∞Â≠óÊàñnull
    }
    let swMap = new Map([
      //z-Áõ¥Êé•Êí≠ÊîæÔºàÂºÄÔºâ
      ["z", (str) => {
        this.rule["‰∫åÁ∫ß"] = "*"
      }],
      //d-ÂÄíÂ∫èÔºàÂºÄÔºâÔºåÈªòËÆ§Ëá™Âä® d0-ÂÄíÂ∫èÔºàÂÖ≥Ôºâ
      ["d", (str) => {
        const digit = checkDigit(str, 'd');
        this.rule["ÂÄíÂ∫è"] = "0"
      }],
      //v-Áõ¥ÈìæÊ†áËÆ∞ÔºàÂºÄÔºâÔºåÊääÊí≠ÊîæÈìæÊé•ÂΩìÊàêVideoÁõ¥Êé•Êí≠ÊîæÔºå‰∏çÂÜçÂóÖÊé¢ÊàñËß£Êûê
      ["v", (str) => {
        this.rule["playParse"] = 0;
      }],
    ])

    function processString(str) {
      for (let [key, handler] of swMap) {
        if (str.includes(key)) {
          handler(str);
        }
      }
    }
    processString(switchs)
  },
