function get_p_node() {
    // ÂºïÂÖ•Âä†ÂØÜËß£ÂØÜÂ∫ì
    eval(getCryptoJS())

    // Ëé∑ÂèñpÁöÑÂä†ÂØÜËäÇÁÇπÊï∞ÊçÆ
    var ciphertextBase64 = fetch('http://9966.fit/newline')
    ciphertextBase64 = ciphertextBase64.replace(/\s+/g, '')

    // Ëß£ÂØÜÂØÜÈí•ÔºàBase64ÁºñÁ†ÅÔºâ
    var keyBase64 = 'XKKhkRfwWO96rRQ3JSqNww=='
    var key = CryptoJS.enc.Base64.parse(keyBase64)

    // Ëß£ÂØÜIVÔºàBase64ÁºñÁ†ÅÔºâ
    var ivBase64 = 'Ri09NSEyXS85Ryg8KD11WQ=='
    var iv = CryptoJS.enc.Base64.parse(ivBase64)

    // Ëß£ÂØÜ
