function save_day_say_cache() {
    let day_say_cache = {}
    let day_say_html = fetch('https://v1.hitokoto.cn', {
        headers: {
            'User-Agent':
                'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/122.0.0.0 Safari/537.36',
        },
    })
    day_say_html = JSON.parse(day_say_html)
    // ÁºìÂ≠òËé∑ÂèñÂÜÖÂÆπÂíåÊó∂Èó¥Âà∞day_say_cache
    day_say_cache['content'] = day_say_html['hitokoto']
    day_say_cache['time'] = Math.floor(Date.now() / 1000)
    globalMap0.putMyVar('daysay', day_say_cache)
    // ËøîÂõûËé∑ÂèñÂÜÖÂÆπ
    return day_say_html['hitokoto']
}

// ÁîüÊàêÂ∏¶ÊØèÊó•‰∏ÄÂè•ÁöÑÊêúÁ¥¢Ê°Ü
function generate_day_say_input(res, interval) {
    let day_say = ''
    let interval_time = interval ? interval : 10
    try {
        let day_say_cache = {}
        let day_time = 0
        if (globalMap0.hasMyVar('daysay')) {
            day_say_cache = globalMap0.getMyVar('daysay', {})
            day_time = day_say_cache['time']
            // Ë∂ÖÊó∂
            if (Math.floor(Date.now() / 1000) - day_time >= interval_time) {
                day_say = save_day_say_cache()
            } else {
                day_say = day_say_cache['content']
            }
        } else {
            day_say = save_day_say_cache()
        }
    } catch {
        day_say = ''
    }

    res.push({
        title: 'ÊêúÁ¥¢',
        col_type: 'input',
        desc: ' üòçÔºö' + day_say,
        url: "'hiker://search?s=' + input + '&rule='+MY_RULE.title",
    })
}

$.exports = {
    generate_day_say_input,
}
