    var sortOrder = obj.sort ? obj.sort : [];
    var filterlist = obj.filist ? obj.filist : [];
    var img = obj.img;
    var texts = contents.map(x => {
      if (x.includes('href')) {
        var list = pdfa(x, 'body&&a').map(k => pdfh(k, 'Text')).slice(0, 5).join(' ');
        if (list == '') {
          return pdfh(x, 'Text');
        } else {
          return pdfh(x, ':matchText&&Text') + list;
        }
      }
      return pdfh(x, 'Text');
    });
    var array = texts.sort((a, b) => {
      const indexA = sortOrder.indexOf(a.substr(0, 2));
      const indexB = sortOrder.indexOf(b.substr(0, 2));
      return indexA - indexB;
    })
    var regex = new RegExp('(' + sortOrder.join('|') + ')');
    array = array.filter(function (item) {
      return regex.test(item);
    });

    var fireg = new RegExp('(' + filterlist.join('|') + ')');
    if (filterlist.length > 0) {
      array = array.filter(function (item) {
        return !fireg.test(item);
      });
    }
    //log(array)

    //array = sortOrder.length==0?array:this.strConvert(array);
    array = this.strConvert(array, obj.sp);

    if (array.length > 6) {
      array = array.slice(0, 6);
    }

    var titles = [];
    var descs = [];

    switch (array.length) {
      case 2:
        titles = array[0];
        descs = array[1];
        break;
      case 3:
        titles = array.slice(0, 1);
        descs = array.slice(1);
        break;
      case 4:
      case 5:
        titles = array.slice(0, 2);
        descs = array.slice(2);
        break;
      case 6:
      default:
        titles = array.slice(0, 3);
        descs = array.slice(3);
        break;
    }
    storage0.putMyVar('er_info', {
      desc: descs.join('\n'),
      img: img,
    })
    d.push({
     // title: this.small(titles[0],custom.标题颜色) + this.small('\n' + titles.slice(1, 3).join('\n'), custom.描述上),
      //desc: this.small(descs.join('\n'), custom.描述下),
          title: this.small('评分：' + pdfh(html, '.star_tips&&Text'),custom.标题颜色) +  this.small( '\n'  + pdfh(html, '.data:contains(年份)&&Text'),custom.描述上),
          desc: this.small(pdfh(html, '.data:contains(状态)&&Text') + '\n' + pdfh(html, '.data:contains(主演)&&Text') + '\n' + pdfh(html, '.data:contains(导演)&&Text'),custom.描述下),
      pic_url: img + '@Referer=',
      url: MY_URL,
      col_type: 'movie_1_vertical_pic_blur',
      extra: {
        id: "detailid",
        gradient: obj.渐变 ? obj.渐变 : false,
      }
    })
    if (obj.desc != undefined) {
      this.setDesc(obj.desc, num);
    }
  },
  setCustom: function (obj, img,colors) {
    var array = [];
    if (obj.constructor == Array) {
      array = obj;
    }
    var titles = [];
    var descs = [];
    if('titles' in obj){
      titles=obj.titles;
    }
    if('descs' in obj){
      descs=obj.descs;
    }

    if(titles.length==0&&descs.length==0){
    array = this.strConvert(array);
    switch (array.length) {
      case 2:
        titles = array[0];
        descs = array[1];
        break;
      case 3:
        titles = array.slice(0, 1);
        descs = array.slice(1);
        break;
      case 4:
      case 5:
        titles = array.slice(0, 2);
        descs = array.slice(2);
        break;
      case 6:
        titles = array.slice(0, 3);
        descs = array.slice(3);
        break;
      }
    }


    storage0.putMyVar('er_info', {
      desc: descs.join('\n'),
      img: img,
    })
    if (colors != undefined) {
      var color_list=[];

      for (var key in colors) {
      var range = key.split('-');
      var start = parseInt(range[0]);
      var end = range[1] ? parseInt(range[1]) : start;
      for (var i = start; i <= end; i++) {
        color_list[i-1] = colors[key];
        }
      }


      if(color_list!=titles.length+descs.length){
        const fillCount = (titles.length+descs.length) - color_list.length; // 需要补齐的个数
        const filledArr = Array(fillCount).fill({}); // 生成填充数组
        color_list=color_list.concat(filledArr);
      }

      var midIndex = Math.floor(color_list.length / 2); 
      var firstHalf = color_list.slice(0, midIndex);
      var secondHalf = color_list.slice(midIndex);

      function textArray(style,textArray) {
        var list=[];
        style.forEach((x, i) => {
        var left = [];
        var right = [];
        var str = textArray[i];
        Object.keys(x).forEach(k => {
            switch (k) {
                case 'color':
                    left.push('<font color="' + x[k] + '">');
                    right.push('</font>');
                    break;
                default:
                    left.push(`<${k}>`);
                    right.push(`</${k}>`);
                break;
                }
              });
             list[i] = left.join('') + str + right.join('');
          });
        return list;
      }
      titles=textArray(firstHalf,titles);
      descs=textArray(secondHalf,descs);

      d.push({
        title:'““””'+titles[0]+'\n'+titles.slice(1,3).join('\n'),
        desc:'““””'+descs.join('\n'),
        img:img+'@Referer='+img,
        url:MY_URL,
        col_type:'movie_1_vertical_pic_blur',
        extra:{
          id:"detailid",
        }
      })

    } else {
      d.push({
        title: titles[0] + '\n' + this.small(titles.slice(1, 3).join('\n')),
        desc: this.small(descs.join('\n')),
        pic_url: img + '@Referer=',
        url: MY_URL,
        col_type: 'movie_1_vertical_pic_blur',
        extra:{
          id: "detailid",
        }
      })
  }
  },
  isColor: function (text) {
    const regex = /^#([0-9A-Fa-f]{3}|[0-9A-Fa-f]{6})$|^([a-z]+)$/i;
    return regex.test(text);
  },
  getPreset: function (custom) {
    if (typeof customs == 'object' && !custom) {
      custom = customs;
    } else {
      custom = custom ? custom : {}
    }
    custom.正序颜色 = (custom.正序颜色 && this.isColor(custom.正序颜色)) ? custom.正序颜色 : '#1AAD19';
    custom.逆序颜色 = (custom.逆序颜色 && this.isColor(custom.逆序颜色)) ? custom.逆序颜色 : '#FF0000';
    custom.图标 = custom.图标 ? custom.图标 : 'https://i2.100024.xyz/2023/06/10/h4z627.webp';
    custom.线路选中 = custom.线路选中 ? custom.线路选中 : '#008B8B';
    custom.线路样式 = custom.线路样式 ? custom.线路样式 : 'scroll_button';
    custom.分割样式 = custom.分割样式 ? custom.分割样式 : 'blank_block';
    custom.简介展开 = custom.简介展开 ? custom.简介展开 : 'black';
    custom.简介收起 = custom.简介收起 ? custom.简介收起 : 'grey';
    Object.assign({}, customs, custom);
