  },
  getLists: function (parse, shsort, h) {
    html = h || html;
    shsort = shsort ? shsort : 'shsort';
    var lists = [];
    var conts = [];
    if (typeof parse.选集 == 'object') {
      conts = parse.选集;
    }
    if (conts.length == 0) {
      try {
        conts = pdfa(html, parse.选集);
      } catch {
        toast('选集定位有误')
      }
    }

    let 替换函数 = '';
    var 选集名称 = parse.选集名称 ? parse.选集名称 : 'a&&Text';
    if (选集名称.includes('@@')) {
      选集名称 = parse.选集名称.split('@@')[0];
      替换函数 = parse.选集名称.split('@@')[1];
    }

    var 选集链接 = parse.选集链接 ? parse.选集链接 : 'a&&href';
    var 解析 = parse.解析 ? parse.解析 : '';
    var temp = [];

    for (var i in conts) {
      if (parse.选集列表 != undefined) {
        if (parse.选集列表 == 'object') {
          lists.push(conts[i].map(x => {
            return {
              title: x.title,
              url: x.url + 解析,
            }
          }))
        } else {
          lists.push(pdfa(conts[i], parse.选集列表).map(x => {
            return {
              title: pdfh(x, 选集名称),
              url: pd(x, 选集链接) + 解析,
            }
          }))
        }
      } else {
        var url = pd(conts[i], 选集链接);
        if (!选集链接.includes('href')) {
          url = pdfh(conts[i], 选集链接);
        }
        let title = pdfh(conts[i], 选集名称);
        title = 替换函数 != '' ? this.evalRe(title, 替换函数) : title;
        temp.push({
          title: title,
          url: url + 解析,
        })
      }
    }
    let 倒序 = parse.倒序 ? parse.倒序 : false;
    var results = lists.length ? lists : [temp];
    if (倒序) {
      results = results.map(x => x.reverse());
    }
    var plist = storage0.getMyVar('er_playlists', '{}');
    if (results.some(x => x.length > 0)) {
      if (!plist.hasOwnProperty(shsort)) {
        plist[shsort] = {};
      }
      plist[shsort]['length'] = results.length;
    };
    storage0.putMyVar('er_playlists', plist);
    return results;
  },
  getPlayers: function (parse, mode, func) {
    //log(mode)
    var play = 'er_playlist_';
    this.preLoad(func);
    this.close(parse.map(x => x.类型), parse.length);
    mode = mode ? mode : 1;

    let pe = [1];
    try {
      pe = parse.map(x => pdfa(html, x.线路).length).filter(y => y > 0);
    } catch { }


    parse.forEach((item, i) => {
      var tabs = [{ title: '主线' }];
      if (item.线路 != undefined) {
        tabs = this.getTabs(item);
      }
      putMyVar(play + i + '_tabs', tabs.map(x => x.title));

      if (item.动态数据) {
        if (mode == 1) {
          this.setTabs(tabs, item.类型, play + i, item);
        } else {
          this.setTabs2(tabs, item.类型, play + i, item, pe);
        }
        putMyVar('er_html', html);
        putMyVar('er_url', MY_URL);
        var info = { parse: item, mark: play + i, url: MY_URL };
        if (tabs.some(x => x.url)) {
          info['urls'] = tabs.map(x => x.url);
        }

        var lists = func(getMyVar('er_' + item.类型, '0'), info);
      } else {
        var lists = this.getLists(item, play + i);

        if (lists.some(x => x.length > 0)) {
          if (mode == 1) {
            this.setTabs(tabs, item.类型, play + i, item);
          } else {
            this.setTabs2(tabs, item.类型, play + i, item, pe);
          }
        }
      }
      if (getMyVar('er_' + item.类型 + 'fold', '1') == '1') {
        this.setLists(lists, getMyVar('er_' + item.类型, '0'), play + i, item);
      }
      d.push({
        col_type: 'blank_block'
      })
    })
  }
}


