  MultiSelectMode: function (name, id, shsort) {
    return $('#noLoading#').lazyRule((datatitle, dataurl, shsort) => {
      function unique(arr) {
        const res = new Map()
        return arr.filter((a) => !res.has(a.url) && res.set(a.url, 1))
      }
      let duoselect = storage0.getMyVar(shsort + "_duoselect") ? storage0.getMyVar(shsort + "_duoselect") : [];
      if (duoselect.findIndex(x => x.url === dataurl) == -1) {
        duoselect.push({
          title: datatitle,
          url: dataurl
        })
        duoselect = unique(duoselect)
        updateItem(dataurl, {
          title: `““””<span style="color:#FF5733">` + datatitle,
        });
      } else {
        function removeByValue(arr, val) {
          for (var i = 0; i < arr.length; i++) {
            if (arr[i]['url'] == val) {
              arr.splice(i, 1);
              break;
            }
          }
        }
        removeByValue(duoselect, dataurl)
        updateItem(dataurl, {
          title: datatitle
        });
      }
      storage0.putMyVar(shsort + "_duoselect", duoselect);
      return "hiker://empty"
    }, name, id, shsort)
  },
}


