var Create = {
    nextPage: function() {
        var result = false;
        try {
            var next = eval(下一页);
            //log('n:'+next)
            if (next == "") {
                throw new Error('下一页链接为空')
            }
            result = true;
            if (true_url == next) {
                result = false;
            }
        } catch (e) {
            //log("可能不存在下一页或者下一页定位有问题");
            log("错误信息:" + e.message);
        }
        if ((next == "" || next == undefined) && page == 1) {
            next = true_url;
        } else {
            //result=false;
            next = getMyVar(状态);
        }
        //log('l'+true_url)
        //log('n'+next)
        if (page != 1) {
            if (true_url == next && !result) {
                result = false;
            } else {
                result = true;
            }
        }
        return result;
        //putMyVar(状态, next);
    },
    getBackImage: function(imgurl) {
        var result = imgurl;
        try {
            result = /url(?:\s+)?\((.*?)\)/g
                .exec(imgurl)[1]
                .replace(/('|"|&quot;)/gm, "");
        } catch {}
        return result;
    },
    httpHandle: function(input) {
        if (input.startsWith("http")) {
            return input;
        } else if (input.startsWith("//")) {
            return "https:" + input;
        } else if (input.startsWith("/")) {
            return MY_HOME + input;
        }
    },
    imgHandle: function(item, rule) {
        var img = "";
        if (rule.img.startsWith("pd")) {
            img = eval(rule.img);
        } else if (rule.backimg) {
            img = pdfh(item, rule.img);
            img = this.getBackImage(img);
            img = this.httpHandle(img);
        } else {
            img = pdfh(item, rule.img);
            if (!img.startsWith("http")) {
                img = pd(item, rule.img);
                img = this.httpHandle(img);
            }
        }
        return img + rule.ref;
    },
    urlHandle: function(item, rule) {
        var href = "";
        if (rule.url.startsWith("pd")) {
            href = eval(rule.url);
        } else {
            href = pd(item, rule.url);
        }
        href = rule.imt ? href + "#immersiveTheme#" : href;
        return href;
    },
    setContent: function(item, input) {
        let output = "";
        if (input == undefined) {
            return output;
        }
        if (input.startsWith("pd")) {
            output = eval(input);
        } else {
            output = pdfh(item, input);
        }
        return output;
    },
    refresh: function() {
        addListener(
            "onRefresh",
            $.toString((u) => {
                clearMyVar("yilist");
                //var p = storage0.getMyVar('spage', '');
                //putMyVar(u, p);
            }, 状态)
        );
    },
    getBooleanValue: function(value, defaultValue) {
        return value !== undefined ? Boolean(value) : (defaultValue !== undefined ? defaultValue : true);
    },
    debuglog: function(i, item, rule, obj) {
        log('----------' + (i + 1) + ':调试开始----------');
        if (item.constructor == Object) {
            item = JSON.stringify(item);
        }
        log('item内容:\r\n' + item + '\r\n' +
            `\n\ttitle定位:  ${rule.title}  ---> 结果:  ${obj.title}` +
            `\n\tdesc定位:  ${rule.desc}  ---> 结果:  ${obj.desc}` +
            `\n\timg定位:  ${rule.img}  ---> 结果:  ${obj.img}` +
            (rule.content != undefined ? `\n\tcontent定位:  ${rule.content}  ---> 结果:  ${obj.content}` : '') +
            `\n\turl定位:  ${rule.url}  ---> 结果:  ${obj.url}`
        );
        log('----------' + (i + 1) + ':调试结束----------');
    },
    jsonList: function(arr, json, rule) {
        if (json.constructor != Object) {
            try {
                json = JSON.parse(json);
            } catch {}
        }
        if (json[rule.list] == undefined) {
            arr.push({
                title: '““””<font color="grey"><small>无内容</small></font>',
                url: "hiker://empty",
                col_type: "text_center_1",
                extra: {},
            });
            return;
        }
        var debug = this.getBooleanValue(rule.debug, false);
        var empty = this.getBooleanValue(rule.empty);
        if (debug) {
            log('定位列表:' + rule.list);
        }
        json[rule.list].forEach((item, i) => {
            var title = eval(rule.title);
            var desc = eval(rule.desc);
            var content = eval(rule.content);
            var img = eval(rule.img) + rule.ref;
            var href = eval(rule.url);
            var url = rule.imt ? href + "#immersiveTheme#" : href;
            var lazy = rule.lazy ? rule.lazy : "";

            var dd = {
                title: title,
                desc: desc,
                content: content,
                pic_url: img,
                url: (empty ? "hiker://empty##" : '') + url,
                col_type: rule.col_type,
                extra: {
                    img: img,
                },
            };

            if (debug) {
                var rx = {
                    title,
                    desc,
                    img,
                    content,
                    url
                };
                this.debuglog(i, item, rule, rx);
            }

            if (lazy != "") {
                dd["url"] = href + lazy;
            }
            arr.push(dd);
        });
    },
    ruleList: function(arr, html, rule) {
        var list = pdfa(html,
            rule.list);
        var debug = this.getBooleanValue(rule.debug,
            false);
        var empty = this.getBooleanValue(rule.empty);
        if (debug) {
            log('定位列表:' + rule.list);
        }
        list.forEach((item, i) => {
            var title = this.setContent(item, rule.title);
            var desc = this.setContent(item, rule.desc);
            var img = this.imgHandle(item, rule);
            var url = this.urlHandle(item, rule);
            var content = this.setContent(item, rule.content);

            if (debug) {
                var rx = {
                    title,
                    desc,
                    img,
                    content,
                    url
                };
                this.debuglog(i, item, rule, rx);
            }

            //var lazy = rule.lazy ? rule.lazy : "";
            var dd = {
                title: title,
                desc: desc,
                pic_url: img,
                url: url + lazy,
                content: content,
                col_type: rule.col_type,
                extra: {
                    img: img,
                },
            };
            if (lazy != "") {
                dd["url"] = url + lazy;
            }
            arr.push(dd);
        });
    },
    List: function(arr, html, rule, param) {
        param = param ? param : {};
        var auto = param.auto ? param.auto : false;
        var show = param.show ? param.show : false;
        if (page == 1) {
            //this.spage.set(true_url);
        }
        this.refresh();
        var debug = this.getBooleanValue(rule.debug, false);
        var empty = this.getBooleanValue(rule.empty);
        var list = pdfa(html, rule.list);
        if (debug) {
            log('定位列表:' + rule.list);
        }
        //log(this.nextPage())
        if ((storage0.getMyVar('yi_isnext', 't') == 't' && list.length > 0) || page == 1) {
            list.forEach((item, i) => {
                deleteItem("pagenum");
                deleteItem("pageline");
                var id = "";
                var cls = "";
                var title = this.setContent(item, rule.title);
                var desc = this.setContent(item, rule.desc);
        var desc0 = this.setContent(item, rule.desc0);
        var desc1 = this.setContent(item, rule.desc1);
                var img = this.imgHandle(item, rule);
                var url = this.urlHandle(item, rule);
                var content = this.setContent(item, rule.content);

                if (debug) {
                    var rx = {
                        title,
                        desc,
                        img,
                        content,
                        url
                    };
                    this.debuglog(i, item, rule, rx);
                }

                if (i == list.length - 1) {
                    id = "lastid_" + page;
                    cls = "lastmark";
                }
                d.push({
                    title: title,
                    desc: desc0 + '\t\t' + desc1 + '\t\t' + desc,
                    pic_url: img,
                    url: url + lazy,
                    content: content,
                    col_type: rule.col_type,
                    extra: {
                        img: img,
                        id: id,
                        cls: cls,
                    },
                });
                if (i == list.length - 1 && show) {
                    d.push({
                        col_type: "line",
                        extra: {
                            id: "pageline",
                        },
                    });

                    d.push({
                        title: "““””<small>加载中...</small>",
                        url: "hiker://empty",
                        col_type: "text_center_1",
                        extra: {
                            id: "pagenum",
                        },
                    });
                }
            });
        } else {
            updateItem("pagenum", {
                title: '““””<font color="grey"><small>到底了</small></font>',
                url: "hiker://empty",
                col_type: "text_center_1",
                extra: {
                    id: "last",
                },
            });
        }
        if (list.length == 0 && page == 1) {
            d.push({
                col_type: "line",
            });
            d.push({
                title: '““””<font color="grey"><small>无内容</small></font>',
                url: "hiker://empty",
                col_type: "text_center_1",
                extra: {},
            });
        }
        if (this.nextPage()) {
            //log('t');
            storage0.putMyVar('yi_isnext', 't');
        } else {
            //log('f');
            storage0.putMyVar('yi_isnext', 'f');
        }
        if (auto) {
            //createNextPage();
            autoPage();
        }
    },
};

var extMethod = {
