let init_cate = []
for (let i = 0; i < 20; i++) {
    init_cate.push("0")
}

// 取四金刚缓存
const cate_temp_json = getMyVar("html.fl", JSON.stringify(init_cate))
const cate_temp = JSON.parse(cate_temp_json)

// 取其他分类列表缓存

const classify_json = getMyVar("html.classify", JSON.stringify(init_cate))
const classify_temp = JSON.parse(classify_json)
if (parseInt(page) === 1) {
    fl.forEach((category, index) => {
        //四大金刚的分类	
        let sub_classify = parseDomForArray(category, 小类定位);
        sub_classify.forEach((item, key) => {
            let title = parseDomForHtml(item, 分类标题)
            d.push({
                title: key.toString() === cate_temp[index] ? '““””<b><font color=' + 分类颜色 + '>' + title + ' </font></b>' : title,
                url: $(parseDom(item, 分类链接)).lazyRule((params) => {
                    params.cate_temp[params.index] = params.key.toString()
                    putMyVar("html.fl", JSON.stringify(params.cate_temp))
                    putMyVar("html.url", input)
                    refreshPage(true)
                    return "hiker://empty"
                }, {
                    cate_temp: cate_temp,
                    index: index,
                    key: key,
                    page: page,
                }),
                col_type: 'scroll_button',
            })
        })
        d.push({
            col_type: "blank_block"
        });
    })

}

