String.prototype.replaceWithCallback = function (searchValue, replaceValue, callback) {
    let returnValue = this;
    if (callback != null) {
        returnValue = callback(this, searchValue, replaceValue);
    }
    return returnValue;
};

//删除重定向图片链接
function deleteInvalidImgTag(context, searchValue,replaceValue) {
    let matchArray = context.match(searchValue);
    let returnValue = context;
    if (matchArray != null) {
        for (let i = 0; i < matchArray.length; i++) {
            let item = matchArray[i];
            /<img.*?(data-original|data-src|src)="(.*?)"([\s\S]*?)>/.test(item);
            let imgUrl = RegExp.$2;
            if (imgUrl.indexOf("placeholder") > -1) {
                /<img.*?(data-original)="(.*?)"([\s\S]*?)>/.test(item);
                imgUrl = RegExp.$2;
            }
            if(imgUrl.indexOf("//")==0){imgUrl = "https:" + imgUrl;}
            if (imgUrl != '') {
                if(getRedirectAddress(imgUrl) != ''){
                    returnValue = returnValue.replace(item,replaceValue);
                }
            }
        }
    }
    return returnValue;
}

