    //JPA等线路挂了已屏蔽，需要可去二级恢复
    //TV线路//FST线路//VOE线路//ST线路//DS线路//VTU线路
    var id = input.split('').reverse().join('');
    var url = `https://lk1.supremejav.com/supjav.php?c=${id}`;
    var referer = `https://lk1.supremejav.com/supjav.php?l=${input}`;
    var location = JSON.parse(fetch(url, {
        headers: {
            "Referer": referer,
        },
        onlyHeaders: true
    })).url.replace(/#.*/, "")
    log(location)

    if (location.includes("emturbovid")) {

        //①TV线路
        var domain = getHome(location)
        var path = fetch(location, {
            headers: {
                'Referer': referer
            }
        }).match(/'.*?(\/.*?\.m3u8)/)[1]
        var playUrl = path.includes("com") ? "https:" + path : getHome(location) + path
        return playUrl + ";{Referer@" + location + "}"

    } else if (/cindyeyefinal|fc2stream|michaelapplysome/.test(location)) {

        //②FST线路
        var html = fetch(location, {
            headers: {
                'Referer': referer
            }
        });
        var script = pdfh(html,"body>script&&Html").replace("eval","")
        var uas = eval(script)
        var jwplayer = uas.match(/"(https?.*m3u8.*?)"/)[1]    
       //log(jwplayer)  
        return jwplayer + ";{Referer@" + location + "}"

    } else if (location.includes("streamtape")) {

        //③ST线路
        var html = fetch(location, {
            headers: {
                'Referer': referer
            }
        })
        var pattern = html.match(/\('#(.*)'\)/)[1]
        var src = "var srclink = " + html.match(new RegExp("'" + pattern + ".*?=([^;]+)"))[1]
        eval(src)
        var link = "https:" + srclink + "&stream=1"
        var urlPlay = JSON.parse(fetch(link, {
            headers: {
                "Referer": location,
            },
            onlyHeaders: true
        })).url
        return urlPlay + ";{Referer@" + location + "}"

    } else if (/d0000d|dood|ds2play/.test(location)) {

        //④DS线路
        var html = fetch(location, {
            headers: {
                'Referer': referer
            }
        })
        var pass_md5 = getHome(location) + html.match(/\/pass_md5[^"']+/)[0]
        var token = html.match(/cookieIndex.*?\'(.*?)\'/)[1]
        var data = fetch(pass_md5, {
            headers: {
                "Referer": location
            }
        })

        function makePlay(token) {
            for (var a = "",
                    t = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789",
                    n = t.length,
                    o = 0; 10 > o; o++)
                a += t.charAt(Math.floor(Math.random() * n));
            return a + "?token=" + token + "&expiry=" + Date.now();
        };
        var urlPlay = data + makePlay(token)
        return urlPlay + "#isVideo=true#;{Referer@" + location + "}"

    } else if (location.includes("vtbe.to")) {

        //⑤VTU线路
        function evaldecode(text) {
            var code = '';
            var s = text.replace(/^eval/, '')
                .replace('return p', 'code+=p')
            eval(s);
            return code;
        }

        const hostRegex = (url) => {
            const match = url.match(/^(?:https?:\/\/)?(?:www\.)?([a-zA-Z0-9-]{1,63}\.)+[a-zA-Z]{2,6}/i);
            return match ? match[0] : url;
        };

        let host = hostRegex(referer)

        var html = fetch(location, {
            headers: {
                Referer: host,
            }
        })
        var iframe = pdfa(html, "iframe");

        if (iframe.length>0) {
            html = fetch(pdfh(iframe[0], "iframe&&src"), {
                headers: {
                    Referer: host,
                }
            })
        }

        var scripts = pdfa(html, "body&&script");
        let script = scripts.filter(x => x.includes('eval')).map(m => pdfh(m, "script&&Html"))[0];
        if(script==undefined){
            return 'toast://无数据'
        }

        let m3u8 = /file:"(.*?)"/g.exec(evaldecode(script))[1];
        return m3u8;

    } else if (/voe/.test(location)) {

        //⑥VOE线路
        var html = fetch(location, {
            headers: {
                'Referer': referer
            }
        })
        var currentUrl = html.match(/https?[^']+/)[0]
        var urlPlay = fetch(currentUrl).match(/https?.*?m3u8[^"]+/)[0]
        return urlPlay + ";{Referer@" + location + "}"

    } else {
        log("革命尚未成功,同志仍需努力!")
        return "confirm://咳,本线路这个地址并没有解析，日志已打印出，敬请查看.js:'hiker://empty' "
    }
})"}]

