                            refreshPage(false)
                            return 'toast://提交成功'
                        }, comment._id),
                        col_type: 'text_2',
                        extra: {
                            lineVisible: false
                        },
                    })
                    picacg.d.push({
                        title: '查看漫画',
                        url: $(picacg.empty+'#immersiveTheme##noHistory#$$fypage').rule((id) => {
                            const picacg = $.require('hiker://page/picacg')
                            picacg.getInfo(id)
                            setResult(picacg.d);
                        }, comment._comic._id),
                        col_type: 'text_2',
                        extra: {
                            lineVisible: false
                        },
                    })
                    picacg.d.push({
                        col_type: 'line',
                    })
                })
                setResult(picacg.d)
            }),
            col_type: 'text_center_1'
        })
        picacg.d.push({
            title: '退出登录',
            url: $(picacg.empty).lazyRule(() => {
                const picacg = $.require('hiker://page/picacg')
                writeFile(picacg.picacg_path, '')
                toast('退出登录')
                back(true)
                return picacg.empty
            }),
            col_type: 'text_center_1'
        })
    },
}

$.exports = picacg
