          url: dataurl
        })
        duoselect = unique(duoselect)
        updateItem(dataurl, {
          title: `““””<span style="color:#FF5733">` + datatitle,
        });
      } else {
        function removeByValue(arr, val) {
          for (var i = 0; i < arr.length; i++) {
            if (arr[i]['url'] == val) {
              arr.splice(i, 1);
              break;
            }
          }
        }
        removeByValue(duoselect, dataurl)
        updateItem(dataurl, {
          title: datatitle
        });
      }
      storage0.putMyVar(shsort + "_duoselect", duoselect);
