    //let picUrl = picUrl;
    picUrl.match(/photos\/(\d+)?\/(\d+)?/);
    let bookId = RegExp.$1;
    let imgId = RegExp.$2;
    if(!bookId||!imgId||Number(bookId)<=220980) return imgInputStream;
