    let width = imgBitmap.getWidth();
    let height = imgBitmap.getHeight();
    let y = Math.floor(height / $num);
    let remainder = height % $num;

    let newImgBitmap = Bitmap.createBitmap(width, height, Bitmap.Config.ARGB_8888);
