        debug: false,

        预设(param) {
            if (param == undefined) {
                param = [];
            }
            Object.keys(param).forEach((property) => {
                // 对每个属性名执行操作
                const value = param[property]; // 获取属性值
                // 在这里执行您的操作
                this[property] = value;
                if (this.debug) {
                    console.log(property + ":" + value);
                }
            });
