                //var p = storage0.getMyVar('spage', '');
                //putMyVar(u, p);
            }, 状态)
        );
    },
    getBooleanValue: function(value, defaultValue) {
        return value !== undefined ? Boolean(value) : (defaultValue !== undefined ? defaultValue : true);
    },
