        log('----------' + (i + 1) + ':调试开始----------');
        if (item.constructor == Object) {
            item = JSON.stringify(item);
        }
        log('item内容:\r\n' + item + '\r\n' +
            `\n\ttitle定位:  ${rule.title}  ---> 结果:  ${obj.title}` +
            `\n\tdesc定位:  ${rule.desc}  ---> 结果:  ${obj.desc}` +
            `\n\timg定位:  ${rule.img}  ---> 结果:  ${obj.img}` +
            (rule.content != undefined ? `\n\tcontent定位:  ${rule.content}  ---> 结果:  ${obj.content}` : '') +
            `\n\turl定位:  ${rule.url}  ---> 结果:  ${obj.url}`
        );
        log('----------' + (i + 1) + ':调试结束----------');
