function dynamicClass(options) {
    const obj = {
        //默认值
        layout: "undefined",
        html: "empty",
        page: 0,
        分类颜色: "#FA7298",
        折叠: "““””<b>" + "∧".fontcolor("#1aad19") + "</b>",
