                    obj.cl.forEach((item) => {
                        clearMyVar(item);
                    });
                    refreshPage();
                }, obj),
            }, ];
        },
        checkParam() {
            var obj = {};
            if (this.page == 0 && typeof page == "undefined") {
                throw new Error("请传入页码");
            } else {
                if (typeof page == "undefined") {
                    obj["页码"] = this.page;
