    if (/embedrise|embedgram/.test(input)) {
        var url = pdfh(request(input), "source&&src");
        return url + "#isVideo=true#;{Referer@" + input + "}"
    } else if (/mmfl|mmsw|javgana|netflavns/.test(input)) {
        var html = fetch(input, {
            headers: {
                'Referer': "https://www.netflav5.com/"
            }
        })
        var url = html.match(/file:\"([^\"]+)\"/)[1]
        return url
    } else if (/emturbovid/.test(input)) {
        var url = request(input).match(/https?:\/\/[^;\"]+\.(m3u8|mp4)/)[0]
        return url + ";get;UTF-8;{Origin@" + url + "&&&&Referer@" + url + "}"
    } else if (/dooood/.test(input)) {
        var location = JSON.parse(fetch(input, {
            onlyHeaders: true
        })).url
        var html = fetch(location, {
            headers: {
                'Referer': input
            }
        })
        var pass_md5 = getHome(location) + html.match(/\/pass_md5[^"']+/)[0]
        var token = html.match(/cookieIndex.*?\'(.*?)\'/)[1]
        var data = fetch(pass_md5, {
            headers: {
                "Referer": location
            }
        })

            function makePlay(token) {
                for (var a = "",
                t = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789",
                n = t.length,
                o = 0; 10 > o; o++)
                a += t.charAt(Math.floor(Math.random() * n));
                return a + "?token=" + token + "&expiry=" + Date.now();
            };
        var urlPlay = data + makePlay(token)
        return urlPlay + "#isVideo=true#;{Referer@" + location + "}"
    } else {
        return "video://" + input
    }
});"}]

