function getFav(url, inf) {
    if (!/http[s]?/.test(url)) {
        toast('不是网址')
        return
    }
    var javai = JavaImporter(java.net.URL, org.jsoup.Jsoup)

    var list = []

    with(javai) {
        try {
            var u = new URL(url);
        } catch (e) {
            log('错误信息：' + e)
            toast('错误信息：' + e)
            return
        }
        var pl = u.getProtocol();
        var hs = u.getHost();
        var newUrl = u.getProtocol().concat("://").concat(u.getHost());
        log(newUrl)

        if (inf != '直连') {
            if (inf.includes('##')) {
                inf = inf.split('##')[1]
            }
            list.push(inf.replace('${url}', newUrl))
            return list
        }

        var fav = newUrl + '/favicon.ico'
        var res = fetch(fav, {
            withStatusCode: true
        })
        log(res)
        var code = 0
        if (res != '') {
            var mes = JSON.parse(res)
        }

        if (mes.error != null) {
            if (mes.error.includes('Unable to resolve host')) {
                log('错误信息：' + mes.error)
                toast('错误信息：' + mes.error)
                return
            }

        }
        code = mes.statusCode
        if (code == 200 && !mes.body.includes('html')) {
            list.push(fav)
        }

        try {
            var document = Jsoup.parse(fetch(newUrl));
        } catch (e) {
            log('错误信息：' + e)
            toast('错误信息：' + e)
            return
        }


        var imgs = document.select("link[type=image/x-icon]");
        log(imgs)
        imgs = Object.keys(imgs).length > 0 ? imgs : document.select("link[rel$=icon]")
        imgs = Object.keys(imgs).length > 0 ? imgs : document.select("meta[itemprop=image]")
        //log(JSON.stringify(imgs))

        var isweb = /(com|cn)/
        var isimg = /.*?(svg|jpg|ico|png|gif|jpeg)$/gm
        for (let x of imgs) {

            var link = x.attr("href")
            if (link == '') {
                link = x.attr('content')
            }
            if (isweb.test(link) && isimg.test(link)) {
                if (link.startsWith('//')) {
                    link = `${pl}:${link}`
                }
            }
            if (link.startsWith('/')) {
                link = `${pl}://${hs}${link}`
            } else if (!link.includes(pl)) {
                link = `${pl}://${hs}/${link}`
            }

            list.push(link)
        }


       /* imgs.forEach((x) => {

        })*/

    }

    function unique(arr) {
        var obj = {};
        return arr.filter(function(item, index, arr) {
            return obj.hasOwnProperty(typeof item + item) ? false : (obj[typeof item + item] = true)
        })
    }



    alist = unique(list)

    return alist
}

$.exports.gfav = getFav"},{"col_type":"movie_3","name":"$获取Favicon·α按钮","path":"btn","rule":"js:

