const QING_TITLE = getMyVar('myCollection'),
    newWindow = readFile('newWindow'),
    typeShow = getMyVar('typeShow', '@'),
    types = { 'all': '聚合', 'video': '视频', 'music': '音频', 'live': '直播', 'cartoon': '漫画', 'read': '阅读', 'picture': '图集', 'news': '资讯', 'tool': '工具', 'other': '其它' }
let el = [{
        title: ' ' + QING_TITLE.bold() + ' &nbsp &nbsp ' + '⚙ 轻合集设置 ⚙'.small(),
        url: 'hiker://page/Config?rule=' + QING_TITLE + '&#noRecordHistory#',
        img: MY_RULE.icon || 'hiker://images/icon1',
        col_type: 'avatar'
    }, {
        url: '"hiker://search?s="+input',
        desc: '这是轻合集的搜索哦~~',
        title: '搜索',
        col_type: 'input',
        extra: {
            rules: $.toString((QING_TITLE, typeShow) => {
                let rules = $.require('hiker://page/dataLoad?rule=' + QING_TITLE)
                if (typeShow[0] == '@') {
                    let filterKey = typeShow.slice(1)
                    if (filterKey) rules = rules.filter((v) => v.title.includes(filterKey))
                } else rules = rules.filter((v) => v.type == typeShow)
                return JSON.stringify(rules)
            }, QING_TITLE, typeShow),
            defaultValue: getMyVar('searchKey', ''),
            onChange: "putMyVar('searchKey',input)"
        }
    }],
    rules = $.require('hiker://page/dataLoad?rule=' + QING_TITLE),
    rulesType = rules.reduce((obj, v) => {
        let type = v.type
        if (type) {
            if (!Array.isArray(obj[type]))
                obj[type] = []
            obj[type].push(v)
        }
        return obj
    }, {})
if (typeShow[0] == '@') {
    let filterKey = typeShow.slice(1)
    if (filterKey) rules = rules.filter((v) => v.title.includes(filterKey))
}
el.push(typeShow[0] == '@' ? {
    title: '““””' + ('全部' + (typeShow[1] ? '[' + typeShow.slice(1) + ']' : '')).bold().fontcolor('#12b668') + ' ' + (rules.length).toString().small(),
    url: $(typeShow.slice(5), '查找小程序关键词').input(() => {
        putMyVar('typeShow', '@' + input)
        refreshPage(false)
        return 'hiker://empty'
    }),
    col_type: 'scroll_button'
} : {
    title: '““””全部 ' + (rules.length).toString().small(),
    url: $('#noLoading#').lazyRule(() => {
        putMyVar('typeShow', '@')
        refreshPage(false)
        return 'toast://再次点击【全部】可查找小程序'
    }),
    col_type: 'scroll_button'
})
for (let k in types)
    if (k in rulesType)
        el.push({
            title: '““””' + (k == typeShow ? types[k].bold().fontcolor('#12b668') : types[k]) + ' ' + (rulesType[k].length).toString().small(),
            url: k == typeShow ? 'hiker://empty' : $('#noLoading#').lazyRule((k) => {
                putMyVar('typeShow', k)
                refreshPage(false)
                return 'hiker://empty'
            }, k),
            col_type: 'scroll_button'
        })

if (typeShow[0] != '@') rules = rulesType[typeShow]
let { paramHandle } = $.require('hiker://page/funcTools?rule=' + QING_TITLE, {})
rules.forEach((v) => {
    let urlParam = v.url.split(';'),
        ruleTmp = {
            title: v.title, url: urlParam.shift(),
            class_name: v.class_name, class_url: v.class_url,
            area_name: v.area_name, area_url: v.area_url,
            year_name: v.year_name, year_url: v.year_url,
            sort_name: v.sort_name, sort_url: v.sort_url
        },
        fypage = ruleTmp.url.includes('fypage') ? 'fypage' : '1',
        domain = (/^https?:\/\/[^/?#]+/i.exec(ruleTmp.url) || ['hiker://empty'])[0],
        d = {
            title: v.title,
            url: 'hiker://page/frameLoad',
            img: v.icon || 'hiker://images/icon1',
            extra: {
                url: domain + '/#noHistory##noRecordHistory####' + fypage +
                    '.js:let { runCode, indexUrl } = $.require("hiker://page/funcTools？？rule=' + QING_TITLE + '", '
                     + JSON.stringify({
                        MY_RULE: base64Encode(JSON.stringify(ruleTmp)),
                        QING_TITLE: QING_TITLE
                    }) + ')；；indexUrl(input);' + paramHandle(urlParam, v.ua),
                RULE: base64Encode(JSON.stringify({ title: v.title })),
                isIndex: true
            }
        }
    if (newWindow) Object.assign(d.extra, {
        newWindow: true,
        windowId: QING_TITLE + '_' + d.title
    })
    el.push(d)
})
