  strConvert:function(arr, sp) {
      sp = sp != undefined ? sp : '/';
      var reg = new RegExp(sp);

      return arr.map(x => {
        str = x;
        if (reg.test(str)) {
          str = str.split(sp).map((val, index) => (index === 0 ? '' : '') + val).slice(0, 5).join(' ').replace(/ $/, '');
        }
        return str
      })
  },
  setContent: function (obj) {
    //log(obj)
    var custom = this.getPreset();
    var num = obj.num != undefined ? obj.num : 100;
    var contents = obj.main;
    if (contents.constructor == String) {
      contents = contents.split('\n');
    }
    var sortOrder = obj.sort ? obj.sort : [];
    var filterlist = obj.filist ? obj.filist : [];
    var img = obj.img;
    var texts = contents.map(x => {
      if (x.includes('href')) {
        var list = pdfa(x, 'body&&a').map(k => pdfh(k, 'Text')).slice(0, 5).join(' ');
        if (list == '') {
          return pdfh(x, 'Text');
        } else {
          return pdfh(x, ':matchText&&Text') + list;
        }
      }
      return pdfh(x, 'Text');
    });
    var array = texts.sort((a, b) => {
      const indexA = sortOrder.indexOf(a.substr(0, 2));
      const indexB = sortOrder.indexOf(b.substr(0, 2));
      return indexA - indexB;
    })
    var regex = new RegExp('(' + sortOrder.join('|') + ')');
    array = array.filter(function (item) {
      return regex.test(item);
    });

    var fireg = new RegExp('(' + filterlist.join('|') + ')');
    if (filterlist.length > 0) {
      array = array.filter(function (item) {
        return !fireg.test(item);
      });
    }
    //log(array)

    //array = sortOrder.length==0?array:this.strConvert(array);
    array = this.strConvert(array, obj.sp);

    if (array.length > 6) {
      array = array.slice(0, 6);
    }

    var titles = [];
    var descs = [];

    switch (array.length) {
      case 2:
        titles = array[0];
        descs = array[1];
        break;
      case 3:
        titles = array.slice(0, 1);
        descs = array.slice(1);
        break;
      case 4:
      case 5:
        titles = array.slice(0, 2);
        descs = array.slice(2);
        break;
      case 6:
      default:
        titles = array.slice(0, 3);
        descs = array.slice(3);
        break;
    }
    storage0.putMyVar('er_info', {
      desc: descs.join('\n'),
      img: img,
    })
    d.push({
      title: '<b>'+titles[0].fontcolor(custom.标题颜色)+'</b>'+this.small( '\n' + titles.slice(1, 3).join('\n'), custom.描述上),
      desc: this.small(descs.join('\n'), custom.描述下),
      pic_url: img + '@Referer=',
      url: MY_URL,
      col_type: 'movie_1_vertical_pic_blur',
      extra: {
        id: "detailid",
        gradient: obj.渐变 ? obj.渐变 : false,
      }
    })
    if (obj.desc != undefined) {
      this.setDesc(obj.desc, num);
    }
  },
  setCustom: function (obj, img,colors) {
    var array = [];
    if (obj.constructor == Array) {
      array = obj;
    }
    var titles = [];
    var descs = [];
    if('titles' in obj){
      titles=obj.titles;
    }
    if('descs' in obj){
      descs=obj.descs;
    }

    if(titles.length==0&&descs.length==0){
    array = this.strConvert(array);
    switch (array.length) {
      case 2:
        titles = array[0];
        descs = array[1];
        break;
      case 3:
        titles = array.slice(0, 1);
        descs = array.slice(1);
        break;
      case 4:
      case 5:
        titles = array.slice(0, 2);
        descs = array.slice(2);
        break;
      case 6:
        titles = array.slice(0, 3);
        descs = array.slice(3);
        break;
      }
    }


    storage0.putMyVar('er_info', {
      desc: descs.join('\n'),
      img: img,
    })
    if (colors != undefined) {
      var color_list=[];

      for (var key in colors) {
      var range = key.split('-');
      var start = parseInt(range[0]);
      var end = range[1] ? parseInt(range[1]) : start;
      for (var i = start; i <= end; i++) {
        color_list[i-1] = colors[key];
        }
      }


      if(color_list!=titles.length+descs.length){
        const fillCount = (titles.length+descs.length) - color_list.length; // 需要补齐的个数
        const filledArr = Array(fillCount).fill({}); // 生成填充数组
        color_list=color_list.concat(filledArr);
      }

      var midIndex = Math.floor(color_list.length / 2); 
      var firstHalf = color_list.slice(0, midIndex);
      var secondHalf = color_list.slice(midIndex);

      function textArray(style,textArray) {
        var list=[];
        style.forEach((x, i) => {
        var left = [];
        var right = [];
        var str = textArray[i];
        Object.keys(x).forEach(k => {
            switch (k) {
                case 'color':
                    left.push('<font color="' + x[k] + '">');
                    right.push('</font>');
                    break;
                default:
                    left.push(`<${k}>`);
                    right.push(`</${k}>`);
                break;
                }
              });
             list[i] = left.join('') + str + right.join('');
          });
        return list;
      }
      titles=textArray(firstHalf,titles);
      descs=textArray(secondHalf,descs);

      d.push({
        title:'““””'+titles[0]+'\n'+titles.slice(1,3).join('\n'),
        desc:'““””'+descs.join('\n'),
        img:img+'@Referer='+img,
        url:MY_URL,
        col_type:'movie_1_vertical_pic_blur',
        extra:{
          id:"detailid",
        }
      })

    } else {
      d.push({
        title: titles[0] + '\n' + this.small(titles.slice(1, 3).join('\n')),
        desc: this.small(descs.join('\n')),
        pic_url: img + '@Referer=',
        url: MY_URL,
        col_type: 'movie_1_vertical_pic_blur',
        extra:{
          id: "detailid",
        }
      })
  }
  },
  isColor: function (text) {
    const regex = /^#([0-9A-Fa-f]{3}|[0-9A-Fa-f]{6})$|^([a-z]+)$/i;
    return regex.test(text);
  },
