var cookies = {};
Object.defineProperty(document, 'cookie', {
    enumerable: true,
    configurable: true,
    get: function() {
        return Object.entries(cookies).map(([key, value]) => `${key}=${value}`).join('; ');
    },
    set: function(value) {
        var list = [];
        if (value.includes(';')) {
            list = value.split(';');
        } else {
            list = [value];
        }
        list.forEach(x => {
            var index = x.indexOf("=");
            var key = x.substring(0, index).trim();
            var value = x.substring(index + 1);
            cookies[key] = value;
        })
    }
});

