}"},{"col_type":"movie_3","name":"批量导入","path":"batchIm","rule":"function batchImport(input, rule) {
    input = input.trim();
    if (input.length < 3 || input[0] != "{") {
        return "toast://请输入正确的格式"
    }
    let im = JSON.parse(input);
    if (im.data == null || im.data.length < 1) {
        return "toast://请输入正确的格式"
    }
    eval(JSON.parse(request("hiker://page/config?rule=" + rule)).rule);
    let config = getConfig();
    let count = 0;
    let all = config.data.map(it => it.url + "&&&").join();
    for (let i of im.data) {
        if (all.indexOf(i.url + "&&&") >= 0) {
            //重复的不要
            continue;
        }
        count++;
        config.data.push(i)
    }
    writeFile("hiker://files/rules/live/config.json", JSON.stringify(config));
    refreshPage();
    return "toast://已导入" + count + "条订阅源";
}"},{"col_type":"movie_3","name":"直播数据","path":"renderData","rule":"function renderLiveData(url) {
    function getReferer(url) {
        if (url.indexOf("gitee") > 0) {
            return "https://" + url.split("://")[1].split("/")[0];
        } else {
            return null
        }
    }
    var txts = fetchCache(url,24, {
            headers: {
                Referer: getReferer(url)
            }
        }).replace(/#EXTINF:-1 ,/g, '')
        .replace(/#EXTINF:-1,/g, '')
        .replace(/\n#genre#/g, ',#genre#')
        .replace(/#EXTINF:-1/g, '')
        .replace(/group-title="/g, '')
        .replace(/\nhttp/g, ',http')
        .replace(/=/g, '')
        .replace(/，#/g, ',#')
        .replace(/",/g, '/')
        .replace(/ /g, '')
        .split('\n');
    const d1 = []
    for (var i = 0; i < txts.length; i++) {
        var r = {};
        var j = txts[i].split(',');
        if (txts[i].indexOf("#EXTINF") == 0 &&
            j.length == 3 && j[2].indexOf("http") == 0) {
            r.title = j[1].split('/')[j[0].split('/').length - 1];
            r.url = j[2].replace(`\n`, '').replace('\r', '');
            if (r.url.indexOf("http") == 0) {
                r.url = r.url + "#isVideo=true#";
            }
            d1.push(r);
            continue;
        }

        if (j.length < 2) {
            continue;
        }
        r.title = j[0].split('/')[j[0].split('/').length - 1];
        r.url = j[1].replace(`\n`, '').replace('\r', '');
        if (r.url.indexOf("http") == 0) {
            r.url = r.url + "#isVideo=true#";
        }
        d1.push(r);
    }
    //多线路
    const d2 = {}
    for (let it of d1) {
        if (d2[it.title] == null) {
            d2[it.title] = []
        }
        d2[it.title].push(it.url)
    }
    return d2;
}

function renderLiveData2(url) {
    function getReferer(url) {
        if (url.indexOf("gitee") > 0) {
            return "https://" + url.split("://")[1].split("/")[0];
        } else {
            return null
        }
    }
    const m3u = fetchCache(url, 24, {
            headers: {
                Referer: getReferer(url)
            }
        }),
        mode = m3u.indexOf('#EXTM3U') == 0,
        re = mode ? /(.*),(.*)\s*?((?:http|rtmp)[^#\s]*)/g : /()(.*),(#genre#|(?:http|rtmp)[^#\s]*)/g;
    let items = {},
        group = '未分组';
    for (let i = re.exec(m3u); i != null; i = re.exec(m3u)) {
        if (mode) {
            group = (/group-title="(.*?)"/.exec(i[1]) || [null, ''])[1];
        } else if (i[3] == '#genre#') {
            group = i[2];
            continue;
        }
        let title = i[2],
            url = i[3].trim() + '#isVideo=true#';
        if (items[group]) {
            if (items[group][title]) {
                items[group][title] = items[group][title].concat([url]);
            } else {
                items[group][title] = [url];
            }
        } else {
            items[group] = {};
            items[group][title] = [url];
        };
    }
    return items;
}

$.exports = {
    renderLiveData: renderLiveData2
