                .exec(imgurl)[1]
                .replace(/('|"|&quot;)/gm, "");
        } catch {}
        return result;
    },
    httpHandle: function(input) {
        if (input.startsWith("http")) {
            return input;
        } else if (input.startsWith("//")) {
            return "https:" + input;
        } else if (input.startsWith("/")) {
            return MY_HOME + input;
        }
    },
    imgHandle: function(item, rule) {
        var img = "";
        if (rule.img.startsWith("pd")) {
            img = eval(rule.img);
        } else if (rule.backimg) {
            img = pdfh(item, rule.img);
            img = this.getBackImage(img);
            img = this.httpHandle(img);
        } else {
            img = pdfh(item, rule.img);
            if (!img.startsWith("http")) {
                img = pd(item, rule.img);
                img = this.httpHandle(img);
            }
        }
        return img + rule.ref;
    },
    urlHandle: function(item, rule) {
        var href = "";
        if (rule.url.startsWith("pd")) {
            href = eval(rule.url);
