    });
    if (decrypt) {
        let decryptedStr = decrypt.toString(CryptoJS.enc.Utf8);
        return decryptedStr;
    } else {
        // 解密失败，返回null或错误信息
        return null;
    }
}

// 加密函数
function Encrypt(plaintext) {
    const key = CryptoJS.enc.Utf8.parse("7205a6c3883caf95b52db5b534e12ec3");
    const iv = CryptoJS.enc.Utf8.parse("81d7beac44a86f43");
    var encrypted = CryptoJS.AES.encrypt(plaintext, key, {
        iv: iv,
        mode: CryptoJS.mode.CFB,
        padding: CryptoJS.pad.NoPadding // 注意这里应该是CryptoJS.pad.NoPadding
    });
    var ciphertext = encrypted.ciphertext.toString(CryptoJS.enc.Hex);
    return ciphertext.toUpperCase();
}

//图片解密
function image(F) {
    // 密钥和初始化向量
    const key = CryptoJS.enc.Utf8.parse('f5d965df75336270');
    const iv = CryptoJS.enc.Utf8.parse('97b60394abc2fbe1');

    // 使用CryptoJS进行AES解密
    const decrypted = CryptoJS.AES.decrypt({
        ciphertext: CryptoJS.enc.Hex.parse(F) // 假设F是十六进制字符串
    }, key, {
        iv: iv,
        mode: CryptoJS.mode.CBC,
        padding: CryptoJS.pad.Pkcs7
    });

    // 检查解密是否成功
    if (decrypted) {
        // 解密成功，将解密后的数据转换为Base64格式
        return decrypted.toString(CryptoJS.enc.Base64);
    } else {
        // 解密失败，返回null或错误信息
        return null;
    }
}

