$.exports = {
    matchCode(text, isG) {
        if (isG) {
            let url = text.match(matchRegG);
            if (url) return url;
            else return [];
        } else {
            let url = text.match(matchReg);
            return url = url && url[0] || "";
        }
    },
    test(text){
        return testReg.test(text);
    }
}
