var Create = {
    nextPage: function() {
        var result = false;
        try {
            var next = eval(下一页);
            //log('n:'+next)
            if (next == "") {
                throw new Error('下一页链接为空')
            }
            result = true;
            if (true_url == next) {
                result = false;
            }
        } catch (e) {
            //log("可能不存在下一页或者下一页定位有问题");
            log("错误信息:" + e.message);
        }
        if ((next == "" || next == undefined) && page == 1) {
            next = true_url;
        } else {
            //result=false;
            next = getMyVar(状态);
        }
        //log('l'+true_url)
        //log('n'+next)
        if (page != 1) {
            if (true_url == next && !result) {
                result = false;
            } else {
                result = true;
            }
        }
        return result;
        //putMyVar(状态, next);
    },
    getBackImage: function(imgurl) {
        var result = imgurl;
        try {
            result = /url(?:\s+)?\((.*?)\)/g
                .exec(imgurl)[1]
                .replace(/('|"|&quot;)/gm, "");
        } catch {}
        return result;
    },
    httpHandle: function(input) {
        if (input.startsWith("http")) {
            return input;
        } else if (input.startsWith("//")) {
            return "https:" + input;
        } else if (input.startsWith("/")) {
            return MY_HOME + input;
        }
    },
    imgHandle: function(item, rule) {
        var img = "";
        if (rule.img.startsWith("pd")) {
            img = eval(rule.img);
        } else if (rule.backimg) {
            img = pdfh(item, rule.img);
            img = this.getBackImage(img);
            img = this.httpHandle(img);
        } else {
            img = pdfh(item, rule.img);
            if (!img.startsWith("http")) {
                img = pd(item, rule.img);
                img = this.httpHandle(img);
            }
        }
        return img + rule.ref;
    },
    urlHandle: function(item, rule) {
        var href = "";
