    }
    let pUrl=playUrl.split(";")[0];//获取抠掉海阔ua等参数的网页播放链接
    let host = getHost(pUrl); // 获取域名
    if(!host){//判断无域名直接不是视频
        return false
    }
    function print(data){
        if(typeof(log)==='undefined'){
            return  fba.log(data)
        }else {
            return log(data)
        }
    }
