let ascii = {};
ascii.unicode1 = function(str) {
    var value = '';
    for (var i = 0; i < str.length; i++) {
        value = value + '&#' + str.charCodeAt(i) + ';';
    }
    return value;
}
ascii.reconvert = function(str) {
    str = str.replace(/(\\u)(\w{1,4})/gi, function($0) {
        return (String.fromCharCode(parseInt((escape($0).replace(/(%5Cu)(\w{1,4})/g, "$2")), 16)));
    });
    str = str.replace(/(&#x)(\w{1,4});/gi, function($0) {
        return String.fromCharCode(parseInt(escape($0).replace(/(%26%23x)(\w{1,4})(%3B)/g, "$2"), 16));
    });
    str = str.replace(/(&#)(\d{1,6});/gi, function($0) {
        return String.fromCharCode(parseInt(escape($0).replace(/(%26%23)(\d{1,6})(%3B)/g, "$2")));
    });

    return str;
}
// $.exports.unicode1 = unicode1;
// $.exports.reconvert = reconvert;
ascii.encode = function(obj) {
    let encode = this.unicode1(obj.decText);
    return encode;
}
ascii.decode = function(obj) {
    let decode = this.reconvert(obj.encText);
    return decode;
}
