let d = [];
let selectColor = '#C04A2F'
// 暗橙 E38C4A

function putKeyIntoMap(key, value) {
    const keyMap = JSON.parse(getVar((getVar("ClassicHeader.id", "classic")+"_all_key_Map"), "{}"));
    keyMap[key] = value;
    putVar((getVar("ClassicHeader.id", "classic") + "_all_key_Map"), JSON.stringify(keyMap));
}

function getHeaderFromJSONObject(obj) {
    for (let key in obj) {
        d.push({
            title: key == getVar(getVar("ClassicHeader.id", "classic"), Object.keys(obj)[0]) ? "<b>““””<font color='" + selectColor +"'>" + key + "</font></b>" : key,
            url: $("#noLoading#").lazyRule((key) => {
                putVar(getVar("ClassicHeader.id", "classic"), key);
                putVar((getVar("ClassicHeader.id", "classic") + "_cur_index"), key);
                refreshPage(false);
                return "hiker://empty"
            }, key),
            col_type: "scroll_button"
        });
    }
    d.push({
        col_type: "blank_block"
    });
    let key = getVar(getVar("ClassicHeader.id", "classic"), Object.keys(obj)[0]);
    putVar((getVar("ClassicHeader.id", "classic") + "_cur_index"), key);
    getNextHeaderFromJSONArray(key, obj[key]);
    return d;
}

function getNextHeaderFromJSONArray(key, array) {
    if (array == undefined || array.length == 0) {
        return;
    }
    let nextHeader = {};
    let defaultValue = "";
    defaultValue = array[0];
    if (Object.prototype.toString.call(array[0]) === '[object Object]') {
        defaultValue = Object.keys(array[0])[0];
    }
    putKeyIntoMap((getVar("ClassicHeader.id", "classic") + "@@" + key), getVar((getVar("ClassicHeader.id", "classic") + "@@" + key), defaultValue))
    putVar((getVar("ClassicHeader.id", "classic") + "_cur_index"), key + "@@" + getVar((getVar("ClassicHeader.id", "classic") + "@@" + key), defaultValue));
    for (let value of array) {
        if (Object.prototype.toString.call(value) === '[object Object]') {
            // log("valueKeys: "+JSON.stringify(Object.keys(value)[0]))
            let valueKey = Object.keys(value)[0];
            d.push({
                title: valueKey == getVar((getVar("ClassicHeader.id", "classic") + "@@" + key), defaultValue) ? "<b>““””<font color='" + selectColor +"'>" + valueKey + "</font></b>" : valueKey,
                url: $("#noLoading#").lazyRule((valueKey, key) => {
                    function putKeyIntoMap(key, value) {
                        const keyMap = JSON.parse(getVar((getVar("ClassicHeader.id", "classic") + "_all_key_Map"), "{}"));
                        keyMap[key] = value;
                        putVar(getVar("ClassicHeader.id", "classic") + "_all_key_Map", JSON.stringify(keyMap));
                    }
                    putVar(getVar("ClassicHeader.id", "classic") + "@@" + key, valueKey);
                    putVar(getVar("ClassicHeader.id", "classic") + "_cur_index", key + "@@" + valueKey);
                    putKeyIntoMap(getVar("ClassicHeader.id", "classic") + "@@" + key, getVar(getVar("ClassicHeader.id", "classic") + "@@" + key, "defaultValue"))
                    refreshPage(false);
                    return "hiker://empty"
                }, valueKey, key),
                col_type: "scroll_button"
            });
            nextHeader[valueKey] = value[valueKey];
        } else {
            d.push({
                title: value == getVar(getVar("ClassicHeader.id", "classic") + "@@" + key, defaultValue) ? "<b>““””<font color='" + selectColor +"'>" + value + "</font></b>" : value,
                url: $("#noLoading#").lazyRule((value, key) => {
                    function putKeyIntoMap(key, value) {
                        const keyMap = JSON.parse(getVar(getVar("ClassicHeader.id", "classic") + "_all_key_Map", "{}"));
                        keyMap[key] = value;
                        putVar(getVar("ClassicHeader.id", "classic") + "_all_key_Map", JSON.stringify(keyMap));
                    }
                    putVar(getVar("ClassicHeader.id", "classic") + "@@" + key, value);
                    putKeyIntoMap(getVar("ClassicHeader.id", "classic") + "@@" + key, getVar(getVar("ClassicHeader.id", "classic") + "@@" + key, "defaultValue"))
                    putVar(getVar("ClassicHeader.id", "classic") + "_cur_index", key + "@@" + value);
                    refreshPage(false);
                    return "hiker://empty"
                }, value, key),
                col_type: "scroll_button"
            });
        }
    }

    d.push({
        col_type: "blank_block"
    });
    if (nextHeader !== undefined && nextHeader.length != 0) {
        getNextHeaderFromJSONArray(key + "@@" + getVar(getVar("ClassicHeader.id", "classic") + "@@" + key, defaultValue), nextHeader[getVar(getVar("ClassicHeader.id", "classic") + "@@" + key, defaultValue)]);
    }
}

