function buildAESUi(d, obj) {
    let config = $.require("hiker://page/aes?rule=" + obj.ruleTitle).config;
    if (obj.type.includes("AES")) {
        config.type.set("AES")
    }
    if (obj.type.includes("DES") && !obj.type.includes("3DES")) {
        config.type.set("DES")
    }
    if (obj.type.includes("3DES")) {
        config.type.set("3DES")
        // https://tool.lmeee.com/jiami/des3 说明：3DES数据块长度为64位，所以IV长度需要为8个字符（ECB模式不用IV），密钥长度为16或24个字符（8个字符以内则结果与DES相同），IV与密钥超过长度则截取，不足则在末尾填充'\0'补足
    }
    d.push({
        title: "加密",
        desc: "请输入明文",
        col_type: 'input',
        extra: {
            // titleVisible: false,
            defaultValue: getVar("crypto_text", ""),
            type: "textarea",
            onChange: 'putVar("crypto_text",input)'
        },
        url: $.toString((obj) => {
            obj.decText = input;
            // log("obj.decText: " + obj.decText)
            let toastText = "加密成功";
            var cipherText = "";
            let Crypto = $.require("hiker://page/aes?rule=" + obj.ruleTitle);
            let config = Crypto.config;
            try {
                Crypto.checkKey(obj);
                Crypto.checkIv(obj);
                obj.tools["AES"] = Crypto.AES;
                obj.tools["DES"] = Crypto.DES;
                obj.tools["3DES"] = Crypto["3DES"];
                var tool = {};
                for (let key in obj.tools) {
                    if (obj.type.includes("3DES")) {
                        tool = obj.tools["3DES"];
                        break;
                    }
                    if (obj.type.includes(key) && !obj.type.includes("3DES")) {
                        tool = obj.tools[key];
                        break;
                    }
                }
                cipherText = tool.encrypt(obj);
                // log("cipherText: " + cipherText)
            } catch (e) {
                cipherText = e.message;
                toastText = "加密失败";
            }
            config.cipherText.set(cipherText)
            refreshPage();
            return 'toast://' + toastText;
        }, obj),
    });
    buildConfigsUI(d, obj);
