  let temsmode = getMyVar("temsmode", "");
  let ruleTestCfg = GM.get("ruleTestCfg", {});

  function drpy_Search(drpy, source) {
    let homeList = [];
    let d = [];
    try {
      homeList = JSON.parse(drpy.search(MY_KEYWORD, false, MY_PAGE)).list || [];
    } catch { }
    let skipEr = drpy.getRule("二级") === "*" && getItem("skipEr", "");
    let pextra = {};
    if (skipEr) {
      pextra = $.require("videoUrl").carryjs(JSON.parse(drpy.play()));
    }

    for (let it of homeList) {
      let cid;
      let id = String(it.vod_id);
      let url = "hiker://page/detailed" + ui_config.mark;

      let extra = {
        //id: cid,
        vodId: id,
        img: it.vod_pic,
        sname: source.name,
        skey: source.key,
        source: source,
      }

      if (skipEr) {
        extra = Object.assign(extra, pextra);

        url = $().lazyRule((source, id) => {
          let url;
          try {
            let DrpyManage = GM.defineModule("DrpyManage");
            let drpy = DrpyManage.getBySource(source);
            url = JSON.parse(drpy.detail(id)).list[0].vod_play_url.split("$")[1];
          } catch (e) {
            url = id.split("@@")[0];
          }
          return $.require("videoUrl").parse(url, id, source);
        }, source, id);
        cid = id;
      }
      //log(it)

      d.push({
        title: it.vod_name,
        desc: it.vod_remarks + "·" + source.name,
        content: it.vod_content,
        url: url,
        pic_url: it.vod_pic,
        col_type: "movie_3",
        extra: extra,
      });
    }
    setResult(d);
  }

  if (temsmode) {
    smode = Number(temsmode);
    clearMyVar("temsmode");
  }
  if (!fileExist("hiker://files/data/" + MY_RULE.title + "/libs_hiker/drpy2.js")) return setResult([{
    title: "请前往小程序主页设置drpyPath",
    url: "hiker://home@" + MY_RULE.title
  }]);

  let runtimeConfig = GM.defineModule("runtimeConfig");
  let DrpyManage = GM.defineModule("DrpyManage");

  //测试开始
  if ("path" in ruleTestCfg) {
    let source = ruleTestCfg;
    log("ss")
    log(source)
    let drpy = DrpyManage.getBySource(source);
    return drpy_Search(drpy, source);
    //测试结束
